%{Express the quadratic equation with ”a”, ”b”, and ”c” and its quadratic formula in MATLAB. Also find the roots of the quadratic equation by assumingthe weights ”a”, ”b”, and ”c” of the expression.%}clc;clear all;close all;%-------------------------------------syms a b c x;a=4;b=-4;c=1;sol=solve(a*x.^2 + b.*x + c==0,x);x1=(-b+sqrt(b.^2-4.*a.*c))/(2.*a);x2=(-b-sqrt(b.^2-4.*a.*c))/(2.*a);